posts_by_date.map:
function(doc) { 
 emit(doc.pub_date, 
 { "pubDate": new Date(doc.pub_date[0], doc.pub_date[1], doc.pub_date[2], doc.pub_date[3], doc.pub_date[4], doc.pub_date[5]), 
 "summary":doc.summary, 
 "post":doc.post }) 
};

thing_templates.map:
function(doc) {
 if (doc.type === "thing" && doc.thing_type && doc.thing_type == "thing_template") {
 emit(doc.thing_template_name, doc);
 }
}
